mapper.select:
SELECT o.id, o.order_no, p.name AS performanceName, DATE_FORMAT(s.session_time, '%Y-%m-%d %H:%i') AS sessionTime, o.grade_name, o.count, o.total_amount, os.status_desc AS statusDesc, o.create_time, o.expire_time FROM `order` o LEFT JOIN performance p ON o.performance_id = p.id LEFT JOIN performance_session s ON o.session_id = s.id LEFT JOIN ( SELECT 0 AS code, '待支付' AS status_desc UNION ALL SELECT 1 AS code, '已支付' AS status_desc UNION ALL SELECT 2 AS code, '已取消' AS status_desc UNION ALL SELECT 3 AS code, '已完成' AS status_desc UNION ALL SELECT 4 AS code, '已退款' AS status_desc ) os ON o.status = os.code WHERE o.user_id = ${@com.ticket.util.UserContext@getCurrentUserId()}
AND o.status = #{status}
ORDER BY o.create_time DESC
